import React, { useState } from 'react';
import axios from 'axios';

function NextWordPrediction() {
    const [text, setText] = useState('');
    const [numWords, setNumWords] = useState(1);
    const [predictedText, setPredictedText] = useState('');
    const [loading, setLoading] = useState(false); // Loading state

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true); // Show loader while waiting for response
        setPredictedText(''); // Clear previous result

        try {
            const response = await axios.post('http://localhost:8000/predict', {
                text: text,
                num_words: parseInt(numWords, 10),
            });
            setPredictedText(response.data.predicted_text);
        } catch (error) {
            console.error('Error predicting next word:', error);
            setPredictedText('Error: Could not generate prediction.');
        } finally {
            setLoading(false); // Hide loader after response
        }
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif',
            width:"500px",height:"600px",border:"5px solid black",
            background: "linear-gradient(to bottom, #ff0000,rgb(213, 213, 237))"
        }}>
            <h1>Next Word Prediction</h1>
            <form onSubmit={handleSubmit} style={{display:"flex", flexDirection:"column"}}>
                <input
                    type="text"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Enter text"
                    style={{ padding: '10px', marginRight: '10px', width: '300px' }}
                />
                <input 
                    type="number"
                    value={numWords}
                    onChange={(e) => setNumWords(e.target.value)}
                    placeholder="Number of words"
                    min="1"
                    style={{ padding: '10px', width: '100px',marginTop:"20px" }}
                />
                <button type="submit" style={{ width:"300px", 
                    marginTop:"30px",padding: '10px', marginLeft: '0px' }}>Predict</button>
            </form>

            {loading && (
                <div style={{ marginTop: '20px', fontSize: '18px', color: 'blue' }}>
                    <p>Loading...</p>
                </div>
            )}

            {!loading && predictedText && (
                <div style={{ marginTop: '20px', fontSize: '18px' }}>
                    <h2>Predicted Text:</h2>
                    <p>{predictedText}</p>
                </div>
            )}
        </div>
    );
}

export default NextWordPrediction;
