import React, { useState } from 'react';
import axios from 'axios';

function NextWordPrediction() {
    const [text, setText] = useState('');
    const [predictedWord, setPredictedWord] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Send POST request to FastAPI backend
            const response = await axios.post('http://localhost:8000/predict', {
                text: text,
            });
            setPredictedWord(response.data.predicted_word);
        } catch (error) {
            console.error('Error predicting next word:', error);
        }
    };

    return (
        <div>
            <h1>Next Word Prediction</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Enter text"
                />
                <button type="submit">Predict Next Word</button>
            </form>
            {predictedWord && (
                <div>
                    <h2>Predicted Next Word:</h2>
                    <p>{predictedWord}</p>
                </div>
            )}
        </div>
    );
}

export default NextWordPrediction;
