import React, { useState } from "react";
import { Fal } from "@fal-ai/client";

// Initialize the Fall AI client
const API_KEY = process.env.REACT_APP_FAL_API_KEY;
const fal = new Fal({ credentials: API_KEY });

const TextToImage = () => {
  const [prompt, setPrompt] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const generateImage = async () => {
    if (!prompt) return alert("Please enter a prompt!");
    setLoading(true);
    setImageUrl("");

    try {
      const result = await fal.run("fal-ai/flux/dev", { input: { prompt } });

      console.log("Generation Complete:", result);
      if (result?.output && result.output.length > 0) {
        setImageUrl(result.output[0]); // Corrected API response handling
      } else {
        alert("Image generation failed. Check API response.");
      }
    } catch (error) {
      console.error("Error generating image:", error);
      alert("Failed to generate image. Check your API key and input.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h2>Text-to-Image Generator (Fall AI)</h2>
      <input
        type="text"
        placeholder="Enter your prompt..."
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
      />
      <button onClick={generateImage} disabled={loading}>
        {loading ? "Generating..." : "Generate Image"}
      </button>

      {imageUrl && (
        <div>
          <h3>Generated Image:</h3>
          <img src={imageUrl} alt="Generated" width="300" />
        </div>
      )}
    </div>
  );
};

export default TextToImage;
