import React, { useState } from "react";
import axios from "axios";

const TextToImage = () => {
  const [prompt, setPrompt] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const generateImage = async () => {
    if (!prompt) return alert("Please enter a prompt!");
    setLoading(true);
    setImageUrl("");

    try {
      const response = await axios.post("http://127.0.0.1:8000/generate-image/", {
        prompt: prompt,
      });

      if (response.data.image_url) {
        setImageUrl(response.data.image_url);
      } else {
        alert("Image generation failed!");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error generating image!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Text-to-Image Generator (Hugging Face)</h2>
      <input
        type="text"
        placeholder="Enter prompt..."
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        style={{ padding: "10px", width: "300px" }}
      />
      <button onClick={generateImage} disabled={loading} style={{ marginLeft: "10px", padding: "10px" }}>
        {loading ? "Generating..." : "Generate"}
      </button>

      {imageUrl && (
        <div>
          <h3>Generated Image:</h3>
          <img src={imageUrl} alt="Generated" width="300" />
        </div>
      )}
    </div>
  );
};

export default TextToImage;
