import React, { useState } from 'react';
import axios from 'axios';

function NextWordPrediction() {
    const [text, setText] = useState('');
    const [numWords, setNumWords] = useState(1); // Default to predicting 1 word
    const [predictedText, setPredictedText] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Send POST request to FastAPI backend
            const response = await axios.post('http://localhost:8000/predict', {
                text: text,
                num_words: numWords,
            });
            setPredictedText(response.data.predicted_text);
        } catch (error) {
            console.error('Error predicting next word:', error);
        }
    };

    return (
        <div>
            <h1>Next Word Prediction</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Enter text"
                />
                <div>
                    <label htmlFor="numWords">How many next words?</label>
                    <input
                        type="number"
                        id="numWords"
                        value={numWords}
                        onChange={(e) => setNumWords(Number(e.target.value))}
                        min="1"
                        max="50"
                    />
                </div>
                <button type="submit">Predict Next Words</button>
            </form>
            {predictedText && (
                <div>
                    <h2>Predicted Text:</h2>
                    <p>{predictedText}</p>
                </div>
            )}
        </div>
    );
}

export default NextWordPrediction;
