import React, { useState } from "react";
import axios from "axios";

const TextToImage = () => {
  const [prompt, setPrompt] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const generateImage = async () => {
    if (!prompt) return alert("Please enter a prompt!");

    setLoading(true);
    setImageUrl("");
    setError("");

    try {
      const response = await axios.post(
        "https://api.deepai.org/api/text2img",
        {
          text: prompt,
        },
        {
          headers: {
            "api-key": "f3051e15-083c-4dec-b215-b2da736df7bd", // Replace with your DeepAI API key
          },
        }
      );

      setImageUrl(response.data.output_url);
    } catch (error) {
      console.error("Error:", error);
      setError("Error generating image!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Text-to-Image Generator (DeepAI)</h2>
      <input
        type="text"
        placeholder="Enter prompt..."
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        style={{ padding: "10px", width: "300px" }}
      />
      <button
        onClick={generateImage}
        disabled={loading}
        style={{ marginLeft: "10px", padding: "10px" }}
      >
        {loading ? "Generating..." : "Generate"}
      </button>

      {loading && !imageUrl && !error && <p>Generating image, please wait...</p>}

      {error && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <p>{error}</p>
        </div>
      )}

      {imageUrl && (
        <div>
          <h3>Generated Image:</h3>
          <img src={imageUrl} alt="Generated" width="300" />
        </div>
      )}
    </div>
  );
};

export default TextToImage;
