import React, { useState } from "react";
import { HfInference } from "@huggingface/inference";

const TextToImage = () => {
  const [prompt, setPrompt] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const generateImage = async () => {
    if (!prompt) return alert("Please enter a prompt!");

    setLoading(true);
    setImageUrl("");
    setError("");

    try {
      const client = new HfInference("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"); // Replace with your Hugging Face API token

      // Call Hugging Face's text-to-image API
      const image = await client.textToImage({
        model: "stable-diffusion-v1-5/stable-diffusion-v1-5", // Use the model of your choice
        inputs: prompt,
        parameters: { num_inference_steps: 50 },
        provider: "hf-inference",
      });

      // Convert the Blob to a URL that can be used in an <img> tag
      const imageUrl = URL.createObjectURL(image);

      setImageUrl(imageUrl);
    } catch (error) {
      console.error("Error:", error);
      setError("Error generating image!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Text-to-Image Generator (Hugging Face)</h2>
      <input
        type="text"
        placeholder="Enter prompt..."
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        style={{ padding: "10px", width: "300px" }}
      />
      <button onClick={generateImage} disabled={loading} style={{ marginLeft: "10px", padding: "10px" }}>
        {loading ? "Generating..." : "Generate"}
      </button>

      {loading && !imageUrl && !error && <p>Model is loading, please wait...</p>}

      {error && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <p>{error}</p>
        </div>
      )}

      {imageUrl && (
        <div>
          <h3>Generated Image:</h3>
          <img src={imageUrl} alt="Generated" width="300" />
        </div>
      )}
    </div>
  );
};

export default TextToImage;
