import React, { useState } from "react";
import { HfInference } from "@huggingface/inference";

const TextToImage = () => {
  const [prompt, setPrompt] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const generateImage = async () => {
    if (!prompt) {
      alert("Please enter a prompt!");
      return;
    }

    setLoading(true);
    setImageUrl("");
    setError("");

    try {
      const client = new HfInference("hf_xxxxxxxxxxxxxxxxxxxxxxx"); // Replace with your Hugging Face API Key

      // Call Hugging Face API
      const image = await client.textToImage({
        model: "stabilityai/stable-diffusion-2", // Correct model name
        inputs: prompt,
        parameters: { num_inference_steps: 10 }, // Lower steps for faster generation
        provider: "hf-inference",
      });

      if (!image) {
        throw new Error("No image returned from API.");
      }

      // Convert Blob to Image URL
      const imageUrl = URL.createObjectURL(image);
      setImageUrl(imageUrl);
    } catch (error) {
      console.error("API Error:", error);
      setError(`Error: ${error.message || "Failed to generate image."}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Text-to-Image Generator (Hugging Face)</h2>
      <input
        type="text"
        placeholder="Enter prompt..."
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        style={{ padding: "10px", width: "300px" }}
      />
      <button onClick={generateImage} disabled={loading} style={{ marginLeft: "10px", padding: "10px" }}>
        {loading ? "Generating..." : "Generate"}
      </button>

      {loading && <p>Generating image, please wait...</p>}

      {error && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <p>{error}</p>
        </div>
      )}

      {imageUrl && (
        <div>
          <h3>Generated Image:</h3>
          <img src={imageUrl} alt="Generated" width="300" />
        </div>
      )}
    </div>
  );
};

export default TextToImage;
